import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Card, Form, Button, Alert, Spinner } from 'react-bootstrap';
import axios from 'axios';

const LoginPage = () => {
    // Saya isi otomatis untuk mempercepat pengetesan Anda
    const [username, setUsername] = useState('admin');
    const [password, setPassword] = useState('admin123');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        // Pesan ini akan muncul di konsol browser (F12)
        console.log('Mencoba login dengan:', { username, password });

        try {
            const response = await axios.post('http://localhost/undangan_digital_platform/backend/api/auth/login.php', {
                username,
                password
            }, {
                withCredentials: true // SANGAT PENTING untuk mengirim dan menerima cookie sesi
            });

            console.log('Response dari server:', response.data);

            if (response.status === 200) {
                // Jika server merespons "OK"
                navigate('/dashboard');
            }
        } catch (err) {
            console.error('Terjadi error saat login:', err);
            if (err.response) {
                // Jika server merespons dengan kode error (401, 404, 500)
                console.error('Data error:', err.response.data);
                setError(err.response.data.message || 'Terjadi kesalahan dari server.');
            } else if (err.request) {
                // Jika request terkirim tapi tidak ada respons (backend mati, URL salah)
                setError('Tidak dapat terhubung ke server. Pastikan backend (XAMPP) berjalan dan URL API sudah benar.');
            } else {
                // Error lainnya
                setError('Terjadi kesalahan yang tidak diketahui.');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container className="d-flex align-items-center justify-content-center" style={{ minHeight: '100vh' }}>
            <Card style={{ width: '400px' }}>
                <Card.Body>
                    <h3 className="text-center mb-4">Admin Panel Login</h3>
                    {error && <Alert variant="danger">{error}</Alert>}
                    <Form onSubmit={handleLogin}>
                        <Form.Group className="mb-3" controlId="formUsername">
                            <Form.Label>Username</Form.Label>
                            <Form.Control 
                                type="text" 
                                placeholder="Masukkan username" 
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control 
                                type="password" 
                                placeholder="Password" 
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </Form.Group>
                        <Button variant="primary" type="submit" className="w-100" disabled={loading}>
                            {loading ? <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" /> : 'Login'}
                        </Button>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
};

export default LoginPage;
