import React, { useState, useEffect } from 'react';
import { Container, Button, Table, Spinner, Alert, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import InvitationForm from '../components/InvitationForm';
import GuestListModal from '../components/GuestListModal';
import RsvpModal from '../components/RsvpModal'; // <-- Impor komponen baru

const API_URL = 'http://localhost/undangan_digital_platform/backend/api/invitations/';

const DashboardPage = () => {
    // ... (semua state yang sudah ada) ...
    const [invitations, setInvitations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [editingInvitation, setEditingInvitation] = useState(null);
    const [showGuestModal, setShowGuestModal] = useState(false);
    const [selectedInvitation, setSelectedInvitation] = useState(null);
    const navigate = useNavigate();

    // --- State baru untuk modal RSVP ---
    const [showRsvpModal, setShowRsvpModal] = useState(false);

    // ... (semua fungsi yang sudah ada: fetchInvitations, handleLogout, handleDelete, dll. tetap sama) ...
    const fetchInvitations = async () => { /* ... */ };
    useEffect(() => { fetchInvitations(); }, []);
    const handleLogout = () => navigate('/');
    const handleDelete = async (id) => { /* ... */ };
    const handleShowForm = async (invitation = null) => { /* ... */ };
    const handleCloseForm = () => { /* ... */ };
    const handleSaveInvitation = async (formData) => { /* ... */ };
    const handleShowGuestModal = (invitation) => {
        setSelectedInvitation(invitation);
        setShowGuestModal(true);
    };
    const handleCloseGuestModal = () => {
        setShowGuestModal(false);
    };

    // --- Fungsi baru untuk membuka modal RSVP ---
    const handleShowRsvpModal = (invitation) => {
        setSelectedInvitation(invitation);
        setShowRsvpModal(true);
    };

    const handleCloseRsvpModal = () => {
        setShowRsvpModal(false);
    };

    return (
        <>
            <header className="dashboard-header">
                <h3>Dashboard Admin</h3>
                <Button variant="outline-danger" onClick={handleLogout}>Logout</Button>
            </header>

            <Container className="py-4">
                {/* ... (Card dan Tabel tetap sama, hanya tombolnya yang diubah) ... */}
                <Card className="mb-4">
                    {/* ... */}
                </Card>
                <Card>
                    <Card.Header as="h5">Daftar Undangan Tersimpan</Card.Header>
                    <Card.Body>
                        <Table striped bordered hover responsive>
                            {/* ... (thead tetap sama) ... */}
                            <tbody>
                                {invitations.length > 0 ? (
                                    invitations.map(inv => (
                                        <tr key={inv.id}>
                                            <td>{inv.id}</td>
                                            <td>{inv.judul}</td>
                                            <td>{inv.mempelai}</td>
                                            <td>
                                                <Button variant="info" size="sm" className="me-2" onClick={() => handleShowGuestModal(inv)}>Tamu</Button>
                                                {/* --- Tombol "RSVP" sekarang berfungsi --- */}
                                                <Button variant="success" size="sm" className="me-2" onClick={() => handleShowRsvpModal(inv)}>RSVP</Button>
                                                <Button variant="warning" size="sm" className="me-2" onClick={() => handleShowForm(inv)}>Edit</Button>
                                                <Button onClick={() => handleDelete(inv.id)} variant="danger" size="sm">Hapus</Button>
                                            </td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr><td colSpan="4" className="text-center">Belum ada undangan yang dibuat.</td></tr>
                                )}
                            </tbody>
                        </Table>
                    </Card.Body>
                </Card>
            </Container>

            {/* ... (Modal InvitationForm dan GuestListModal tetap sama) ... */}
            {showForm && ( <InvitationForm /* ... */ /> )}
            {showGuestModal && ( <GuestListModal show={showGuestModal} onHide={handleCloseGuestModal} invitation={selectedInvitation} /> )}

            {/* --- Render Modal RSVP --- */}
            {showRsvpModal && (
                <RsvpModal
                    show={showRsvpModal}
                    onHide={handleCloseRsvpModal}
                    invitation={selectedInvitation}
                />
            )}
        </>
    );
};

export default DashboardPage;
