import React, { useState, useEffect } from 'react';
import { Container, Button, Table, Spinner, Alert, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import api from '../api';

import InvitationForm from '../components/InvitationForm';
import GuestListModal from '../components/GuestListModal';
import RsvpModal from '../components/RsvpModal';

const DashboardPage = () => {
    const [invitations, setInvitations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const [activeModal, setActiveModal] = useState(null);
    const [selectedInvitationData, setSelectedInvitationData] = useState(null);

    const fetchInvitations = async () => {
        setLoading(true);
        try {
            const response = await api.get('/invitations/read_all.php');
            setInvitations(response.data.data || []);
        } catch (err) {
            setError('Gagal memuat data undangan.');
        } finally {
            setLoading(false);
        }
    };
    
    useEffect(() => {
        fetchInvitations();
    }, []);

    const handleCloseModals = () => {
        setActiveModal(null);
        setSelectedInvitationData(null);
    };

    // ====================================================================
    // "KAMERA PENGINTAI" DIPASANG DI SINI
    // ====================================================================
    const handleShowForm = async (invitationSummary = null) => {
        // Bersihkan konsol untuk log yang bersih setiap kali tombol diklik
        console.clear(); 
        console.log('%c[Dashboard] Tombol "Buat Undangan Baru" atau "Edit" diklik.', 'color: blue; font-weight: bold; font-size: 14px;');

        if (invitationSummary && invitationSummary.id) {
            console.log('[Dashboard] Mode EDIT. Mengambil data lengkap...');
            try {
                const response = await api.get(`/invitations/read_single.php?id=${invitationSummary.id}`);
                console.log('%c[Dashboard] BERHASIL! Data lengkap diterima.', 'color: green; font-weight: bold;');
                setSelectedInvitationData(response.data);
                setActiveModal('FORM');
            } catch (err) {
                console.error('%c[Dashboard] GAGAL mengambil data lengkap.', 'color: red; font-weight: bold;', err);
                alert('Gagal mengambil detail undangan.');
            }
        } else {
            console.log('[Dashboard] Mode BUAT BARU. Mengatur state untuk menampilkan modal...');
            setSelectedInvitationData(null);
            setActiveModal('FORM');
            console.log('[Dashboard] State diatur. `activeModal` seharusnya sekarang "FORM".');
        }
    };

    const handleSaveInvitation = async (formData) => { /* ... (Fungsi ini tetap sama) ... */ };
    const handleLogout = async () => { /* ... (Fungsi ini tetap sama) ... */ };
    const handleDelete = async (id) => { /* ... (Fungsi ini tetap sama) ... */ };
    const handleShowGuestModal = (invitationSummary) => { setSelectedInvitationData(invitationSummary); setActiveModal('GUEST'); };
    const handleShowRsvpModal = (invitationSummary) => { setSelectedInvitationData(invitationSummary); setActiveModal('RSVP'); };

    console.log(`%c[Dashboard] Render Cycle. Nilai activeModal saat ini: ${activeModal}`, 'color: gray');

    return (
        <>
            {/* ... (Isi JSX tidak ada perubahan, semua tombol dan tabel tetap sama) ... */}
            <header className="dashboard-header"><h3>Dashboard Admin</h3><Button variant="outline-danger" onClick={handleLogout}>Logout</Button></header>
            <Container className="py-4">
                <Card className="mb-4"><Card.Body><Card.Title>Manajemen Undangan</Card.Title><Button variant="primary" onClick={() => handleShowForm()}>+ Buat Undangan Baru</Button></Card.Body></Card>
                <Card><Card.Header as="h5">Daftar Undangan Tersimpan</Card.Header><Card.Body>
                    {loading && <div className="text-center my-5"><Spinner animation="border" /></div>}
                    {error && <Alert variant="danger">{error}</Alert>}
                    {!loading && !error && (
                        <Table striped bordered hover responsive>
                            <thead><tr><th>ID</th><th>Judul</th><th>Mempelai</th><th style={{width: '350px'}}>Aksi</th></tr></thead>
                            <tbody>
                                {invitations.map(inv => (
                                    <tr key={inv.id}><td>{inv.id}</td><td>{inv.judul || '(Tanpa Judul)'}</td><td>{inv.mempelai}</td><td><Button variant="info" size="sm" className="me-2" onClick={() => handleShowGuestModal(inv)}>Tamu</Button><Button variant="success" size="sm" className="me-2" onClick={() => handleShowRsvpModal(inv)}>RSVP</Button><Button variant="warning" size="sm" className="me-2" onClick={() => handleShowForm(inv)}>Edit</Button><Button onClick={() => handleDelete(inv.id)} variant="danger" size="sm">Hapus</Button></td></tr>
                                ))}
                            </tbody>
                        </Table>
                    )}
                </Card.Body></Card>
            </Container>
            <InvitationForm show={activeModal === 'FORM'} onHide={handleCloseModals} onSave={handleSaveInvitation} invitationData={selectedInvitationData} />
            <GuestListModal show={activeModal === 'GUEST'} onHide={handleCloseModals} invitation={selectedInvitationData} />
            <RsvpModal show={activeModal === 'RSVP'} onHide={handleCloseModals} invitation={selectedInvitationData} />
        </>
    );
};

export default DashboardPage;
