import React, { useState, useEffect } from 'react';
import { Container, Button, Table, Spinner, Alert, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
// Hapus 'import axios from 'axios';' dan ganti dengan ini:
import api from '../api'; // <-- Menggunakan konfigurasi API terpusat kita

import InvitationForm from '../components/InvitationForm';
import GuestListModal from '../components/GuestListModal';
import RsvpModal from '../components/RsvpModal';

const DashboardPage = () => {
    // ... (semua state tetap sama) ...
    const [invitations, setInvitations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showFormModal, setShowFormModal] = useState(false);
    const [showGuestModal, setShowGuestModal] = useState(false);
    const [showRsvpModal, setShowRsvpModal] = useState(false);
    const [selectedInvitation, setSelectedInvitation] = useState(null);
    const navigate = useNavigate();

    const fetchInvitations = async () => {
        setLoading(true);
        setError('');
        try {
            // Gunakan 'api' bukan 'axios'
            const response = await api.get('/invitations/read_all.php');
            setInvitations(response.data.data || []);
        } catch (err) {
            setError('Gagal memuat data undangan. Sesi mungkin telah berakhir, coba login ulang.');
            console.error("Error di fetchInvitations:", err);
        } finally {
            setLoading(false);
        }
    };
    
    useEffect(() => {
        fetchInvitations();
    }, []);

    // --- Semua fungsi lain sekarang akan menggunakan 'api' juga ---
    const handleShowForm = async (invitation = null) => {
        if (invitation) {
            try {
                const response = await api.get(`/invitations/read_single.php?id=${invitation.id}`);
                setSelectedInvitation(response.data);
            } catch (err) { /* ... */ }
        } else {
            setSelectedInvitation(null);
        }
        setShowFormModal(true);
    };
    
    const handleSaveInvitation = async (formData) => {
        try {
            const endpoint = formData.id ? '/invitations/update.php' : '/invitations/create.php';
            const response = await api.post(endpoint, formData);
            alert(response.data.message);
            handleCloseModals();
            fetchInvitations();
        } catch (err) { /* ... */ }
    };
    
    const handleDelete = async (id) => {
        if (window.confirm('Yakin ingin menghapus?')) {
            try {
                await api.delete(`/invitations/delete.php?id=${id}`);
                fetchInvitations();
            } catch (err) { /* ... */ }
        }
    };

    // ... (sisa fungsi handle... tetap sama, tidak perlu diubah) ...
    const handleCloseModals = () => { /*...*/ };
    const handleShowGuestModal = (invitation) => { /*...*/ };
    const handleShowRsvpModal = (invitation) => { /*...*/ };

    return (
        <>
            {/* ... (Isi JSX tidak ada perubahan, semua tombol dan tabel tetap sama) ... */}
            <header className="dashboard-header"><h3>Dashboard Admin</h3><Button variant="outline-danger" onClick={() => navigate('/')}>Logout</Button></header>
            <Container className="py-4">
                <Card className="mb-4"><Card.Body><Card.Title>Manajemen Undangan</Card.Title><Button variant="primary" onClick={() => handleShowForm()}>+ Buat Undangan Baru</Button></Card.Body></Card>
                <Card><Card.Header as="h5">Daftar Undangan Tersimpan</Card.Header><Card.Body>
                    {loading && <div className="text-center my-5"><Spinner animation="border" /></div>}
                    {error && <Alert variant="danger">{error}</Alert>}
                    {!loading && !error && (
                        <Table striped bordered hover responsive>
                            <thead><tr><th>ID</th><th>Judul</th><th>Mempelai</th><th style={{width: '350px'}}>Aksi</th></tr></thead>
                            <tbody>
                                {invitations.length > 0 ? (
                                    invitations.map(inv => (
                                        <tr key={inv.id}>
                                            <td>{inv.id}</td>
                                            <td>{inv.judul}</td>
                                            <td>{inv.mempelai}</td>
                                            <td>
                                                <Button variant="info" size="sm" className="me-2" onClick={() => handleShowGuestModal(inv)}>Tamu</Button>
                                                <Button variant="success" size="sm" className="me-2" onClick={() => handleShowRsvpModal(inv)}>RSVP</Button>
                                                <Button variant="warning" size="sm" className="me-2" onClick={() => handleShowForm(inv)}>Edit</Button>
                                                <Button onClick={() => handleDelete(inv.id)} variant="danger" size="sm">Hapus</Button>
                                            </td>
                                        </tr>
                                    ))
                                ) : ( <tr><td colSpan="4" className="text-center">Belum ada undangan yang dibuat.</td></tr> )}
                            </tbody>
                        </Table>
                    )}
                </Card.Body></Card>
            </Container>
            {showFormModal && <InvitationForm show={showFormModal} onHide={handleCloseModals} onSave={handleSaveInvitation} invitationData={selectedInvitation} />}
            {showGuestModal && <GuestListModal show={showGuestModal} onHide={handleCloseModals} invitation={selectedInvitation} />}
            {showRsvpModal && <RsvpModal show={showRsvpModal} onHide={handleCloseModals} invitation={selectedInvitation} />}
        </>
    );
};

export default DashboardPage;
