import React, { useState, useEffect, useRef } from 'react';
import { Modal, Button, Table, Form, InputGroup, Spinner, Alert } from 'react-bootstrap';
import api from '../api';
import Papa from 'papaparse'; // Tetap dibutuhkan untuk CSV

const GuestListModal = ({ show, onHide, invitation }) => {
    const [guests, setGuests] = useState([]);
    const [newGuestName, setNewGuestName] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const fileInputRef = useRef(null);

    const fetchGuests = async () => { /* ... (Fungsi ini tetap sama) ... */ };
    useEffect(() => { if (show) { fetchGuests(); } }, [show, invitation]);
    const handleAddGuest = async (e) => { /* ... (Fungsi ini tetap sama) ... */ };
    const handleDeleteGuest = async (guestId) => { /* ... (Fungsi ini tetap sama) ... */ };
    const handleCopyLink = (guestName) => { /* ... (Fungsi ini tetap sama) ... */ };

    // ======================================================
    // FUNGSI IMPOR YANG DIPERBARUI
    // ======================================================
    const handleFileImport = (e) => {
        const file = e.target.files[0];
        if (!file || !invitation?.id) return;

        // Cek tipe file
        if (file.type === 'text/csv' || file.name.endsWith('.csv')) {
            // Proses sebagai CSV
            Papa.parse(file, {
                header: true,
                skipEmptyLines: true,
                complete: (results) => {
                    const names = results.data.map(row => row.Nama || row.Name).filter(Boolean);
                    if (names.length > 0) {
                        uploadGuestNames(names);
                    } else {
                        alert('Tidak ada nama valid. Pastikan ada kolom "Nama" atau "Name".');
                    }
                }
            });
        } else if (file.type === 'text/plain' || file.name.endsWith('.txt')) {
            // Proses sebagai TXT
            const reader = new FileReader();
            reader.onload = (event) => {
                const text = event.target.result;
                // Pisahkan nama berdasarkan baris baru, hapus baris kosong
                const names = text.split(/\r?\n/).map(name => name.trim()).filter(Boolean);
                if (names.length > 0) {
                    uploadGuestNames(names);
                } else {
                    alert('File TXT kosong atau tidak berisi nama yang valid.');
                }
            };
            reader.readAsText(file);
        } else {
            alert('Format file tidak didukung. Silakan gunakan .csv atau .txt');
        }

        e.target.value = null; // Reset input file
    };

    // Fungsi helper untuk mengirim nama ke backend
    const uploadGuestNames = async (names) => {
        try {
            await api.post('/guests/create.php', {
                invitation_id: invitation.id,
                guests: names
            });
            alert(`${names.length} tamu berhasil diimpor.`);
            fetchGuests(); // Muat ulang daftar
        } catch (err) {
            alert('Gagal mengimpor tamu.');
        }
    };

    return (
        <Modal show={show} onHide={onHide} size="lg">
            <Modal.Header closeButton>
                <Modal.Title>Daftar Tamu untuk: {invitation?.judul}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="d-flex justify-content-between mb-3 flex-wrap gap-2">
                    {/* ... (Form tambah tamu manual tetap sama) ... */}
                    <div>
                        {/* Input file sekarang menerima .csv dan .txt */}
                        <input 
                            type="file" 
                            accept=".csv,.txt" 
                            ref={fileInputRef} 
                            onChange={handleFileImport} 
                            style={{ display: 'none' }} 
                        />
                        <Button variant="outline-success" onClick={() => fileInputRef.current.click()}>Impor File</Button>
                    </div>
                </div>
                {/* ... (Tabel daftar tamu tetap sama) ... */}
            </Modal.Body>
        </Modal>
    );
};

export default GuestListModal;
