import React, { useState, useEffect } from 'react';
import { Container, Button, Table, Spinner, Alert, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import InvitationForm from '../components/InvitationForm';

const API_URL = 'http://localhost/undangan_digital_platform/backend/api/invitations/';

const DashboardPage = () => {
    const [invitations, setInvitations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [editingInvitation, setEditingInvitation] = useState(null);
    const navigate = useNavigate();

    const fetchInvitations = async () => {
        setLoading(true);
        try {
            const response = await axios.get(`${API_URL}read_all.php`, { withCredentials: true });
            setInvitations(response.data.data || []);
        } catch (err) {
            setError('Gagal memuat data undangan.');
        } finally {
            setLoading(false);
        }
    };
    
    useEffect(() => {
        fetchInvitations();
    }, []);

    const handleLogout = () => {
        navigate('/');
    };

    const handleDelete = async (id) => {
        if (window.confirm('Apakah Anda yakin ingin menghapus undangan ini?')) {
            try {
                await axios.delete(`${API_URL}delete.php?id=${id}`, { withCredentials: true });
                fetchInvitations();
            } catch (err) {
                alert('Gagal menghapus undangan.');
            }
        }
    };

    // ====================================================================
    // PERUBAHAN UTAMA 1
    // ====================================================================
    const handleShowForm = async (invitation = null) => {
        if (invitation) {
            // Jika ini mode EDIT, ambil detail lengkapnya dulu
            try {
                const response = await axios.get(`${API_URL}read_single.php?id=${invitation.id}`, { withCredentials: true });
                setEditingInvitation(response.data); // Simpan data lengkap untuk form
            } catch (err) {
                alert('Gagal mengambil detail undangan.');
                return;
            }
        } else {
            // Jika mode CREATE, kosongkan data
            setEditingInvitation(null);
        }
        setShowForm(true);
    };

    const handleCloseForm = () => {
        setShowForm(false);
        setEditingInvitation(null);
    };

    // ====================================================================
    // PERUBAHAN UTAMA 2
    // ====================================================================
    const handleSaveInvitation = async (formData) => {
        try {
            let response;
            if (editingInvitation) {
                // Panggil API UPDATE jika sedang mengedit
                response = await axios.post(`${API_URL}update.php`, formData, { withCredentials: true });
            } else {
                // Panggil API CREATE jika membuat baru
                response = await axios.post(`${API_URL}create.php`, formData, { withCredentials: true });
            }
            alert(response.data.message);
            handleCloseForm();
            fetchInvitations();
        } catch (err) {
            alert(err.response?.data?.message || 'Terjadi kesalahan saat menyimpan.');
        }
    };

    return (
        <>
            <header className="dashboard-header">
                <h3>Dashboard Admin</h3>
                <Button variant="outline-danger" onClick={handleLogout}>Logout</Button>
            </header>

            <Container className="py-4">
                <Card className="mb-4">
                    <Card.Body>
                        <Card.Title>Manajemen Undangan</Card.Title>
                        <Button variant="primary" onClick={() => handleShowForm()}>+ Buat Undangan Baru</Button>
                    </Card.Body>
                </Card>

                <Card>
                    <Card.Header as="h5">Daftar Undangan Tersimpan</Card.Header>
                    <Card.Body>
                        {loading && <div className="text-center my-5"><Spinner animation="border" /></div>}
                        {error && <Alert variant="danger">{error}</Alert>}
                        {!loading && !error && (
                            <Table striped bordered hover responsive>
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Judul</th>
                                        <th>Mempelai</th>
                                        <th style={{width: '350px'}}>Aksi</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {invitations.length > 0 ? (
                                        invitations.map(inv => (
                                            <tr key={inv.id}>
                                                <td>{inv.id}</td>
                                                <td>{inv.judul}</td>
                                                <td>{inv.mempelai}</td>
                                                <td>
                                                    <Button variant="info" size="sm" className="me-2">Tamu</Button>
                                                    <Button variant="success" size="sm" className="me-2">RSVP</Button>
                                                    <Button variant="warning" size="sm" className="me-2" onClick={() => handleShowForm(inv)}>Edit</Button>
                                                    <Button onClick={() => handleDelete(inv.id)} variant="danger" size="sm">Hapus</Button>
                                                </td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr>
                                            <td colSpan="4" className="text-center">Belum ada undangan yang dibuat.</td>
                                        </tr>
                                    )}
                                </tbody>
                            </Table>
                        )}
                    </Card.Body>
                </Card>
            </Container>

            {showForm && (
                <InvitationForm 
                    show={showForm}
                    onHide={handleCloseForm}
                    onSave={handleSaveInvitation}
                    invitationData={editingInvitation}
                />
            )}
        </>
    );
};

export default DashboardPage;
