import React, { useState, useEffect, useRef } from 'react';
import { Modal, Button, Table, Form, InputGroup, Spinner, Alert } from 'react-bootstrap';
import api from '../api';
import Papa from 'papaparse';

const GuestListModal = ({ show, onHide, invitation }) => {
    const [guests, setGuests] = useState([]);
    const [newGuestName, setNewGuestName] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const fileInputRef = useRef(null);

    const fetchGuests = async () => {
        // Pastikan 'invitation' dan 'invitation.id' ada sebelum fetch
        if (!invitation?.id) {
            setError("Informasi undangan tidak valid.");
            return;
        }
        setLoading(true);
        setError('');
        try {
            const response = await api.get(`/guests/read.php?invitation_id=${invitation.id}`);
            // Pastikan data yang diterima adalah array untuk mencegah error
            setGuests(Array.isArray(response.data) ? response.data : []);
        } catch (err) {
            setError('Gagal memuat daftar tamu.');
            setGuests([]); // Jika error, pastikan state adalah array kosong
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (show) {
            fetchGuests();
        }
    }, [show, invitation]);

    const handleAddGuest = async (e) => {
        e.preventDefault();
        if (!newGuestName.trim() || !invitation?.id) return;
        
        try {
            await api.post('/guests/create.php', {
                invitation_id: invitation.id,
                guests: [newGuestName] // Kirim sebagai array
            });
            setNewGuestName('');
            fetchGuests(); // Muat ulang daftar
        } catch (err) {
            alert('Gagal menambah tamu.');
        }
    };

    const handleDeleteGuest = async (guestId) => {
        if (window.confirm('Yakin ingin menghapus tamu ini?')) {
            try {
                await api.post('/guests/delete.php', { id: guestId });
                fetchGuests(); // Muat ulang daftar
            } catch (err) {
                alert('Gagal menghapus tamu.');
            }
        }
    };

    const handleCopyLink = (guestName) => {
        // Menggunakan URL root dari window untuk fleksibilitas
        const baseUrl = window.location.origin.replace(':3000', ''); // Hapus port jika ada
        const url = `${baseUrl}/undangan_digital_platform/public-site/undangan.html?id=${invitation.id}&to=${encodeURIComponent(guestName)}`;
        navigator.clipboard.writeText(url).then(() => {
            alert(`Link untuk ${guestName} berhasil disalin!`);
        });
    };

    const handleImportCSV = (e) => {
        const file = e.target.files[0];
        if (!file || !invitation?.id) return;

        Papa.parse(file, {
            header: true,
            skipEmptyLines: true,
            complete: async (results) => {
                const names = results.data.map(row => row.Nama || row.Name).filter(Boolean);
                if (names.length === 0) {
                    alert('Tidak ada nama yang valid ditemukan di file CSV. Pastikan ada kolom bernama "Nama" atau "Name".');
                    return;
                }
                try {
                    await api.post('/guests/create.php', {
                        invitation_id: invitation.id,
                        guests: names
                    });
                    alert(`${names.length} tamu berhasil diimpor.`);
                    fetchGuests(); // Muat ulang daftar
                } catch (err) {
                    alert('Gagal mengimpor tamu dari CSV.');
                }
            }
        });
        e.target.value = null; // Reset input file
    };
    
    // Jangan render apapun jika tidak disuruh tampil
    if (!show) {
        return null;
    }

    return (
        <Modal show={show} onHide={onHide} size="lg">
            <Modal.Header closeButton>
                {/* Gunakan optional chaining (?) untuk keamanan */}
                <Modal.Title>Daftar Tamu untuk: {invitation?.judul || 'Undangan'}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="d-flex justify-content-between mb-3 flex-wrap gap-2">
                    <Form onSubmit={handleAddGuest} className="d-flex">
                        <InputGroup>
                            <Form.Control
                                type="text"
                                placeholder="Nama Tamu Baru"
                                value={newGuestName}
                                onChange={(e) => setNewGuestName(e.target.value)}
                            />
                            <Button variant="primary" type="submit">Tambah</Button>
                        </InputGroup>
                    </Form>
                    <div>
                        <input type="file" accept=".csv" ref={fileInputRef} onChange={handleImportCSV} style={{ display: 'none' }} />
                        <Button variant="outline-success" onClick={() => fileInputRef.current.click()}>Impor CSV</Button>
                    </div>
                </div>

                {loading && <div className="text-center"><Spinner animation="border" /></div>}
                {error && <Alert variant="danger">{error}</Alert>}
                {!loading && !error && (
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr>
                                <th>Nama Tamu</th>
                                <th style={{ width: '200px' }}>Aksi</th>
                            </tr>
                        </thead>
                        <tbody>
                            {guests.length > 0 ? guests.map(guest => (
                                <tr key={guest.id}>
                                    <td>{guest.nama_tamu}</td>
                                    <td>
                                        <Button variant="secondary" size="sm" className="me-2" onClick={() => handleCopyLink(guest.nama_tamu)}>Salin Link</Button>
                                        <Button variant="danger" size="sm" onClick={() => handleDeleteGuest(guest.id)}>Hapus</Button>
                                    </td>
                                </tr>
                            )) : (
                                <tr>
                                    <td colSpan="2" className="text-center">Belum ada tamu yang ditambahkan.</td>
                                </tr>
                            )}
                        </tbody>
                    </Table>
                )}
            </Modal.Body>
        </Modal>
    );
};

export default GuestListModal;
