import React, { useState, useEffect } from 'react';
import { Modal, Button, Form, Row, Col, Spinner, Alert, Image } from 'react-bootstrap';
import axios from 'axios';

const API_URL = 'http://localhost/undangan_digital_platform/backend/api/';

const InvitationForm = ({ show, onHide, onSave, invitationData }) => {
    const initialFormState = {
        judul: '',
        template_tema: 'classic_elegant.css',
        mempelai: {
            nama_pria: '', panggilan_pria: '', ayah_pria: '', ibu_pria: '', foto_pria: '',
            nama_wanita: '', panggilan_wanita: '', ayah_wanita: '', ibu_wanita: '', foto_wanita: ''
        },
        acara: [{ jenis_acara: 'Akad Nikah', tanggal: '', waktu: '', nama_tempat: '', link_gmaps: '' }],
        amplop: [{ tipe_hadiah: 'BCA', nomor_rekening: '', atas_nama: '' }],
        cerita: '',
        media: [],
        cover_slideshow: [],
        musik_latar: ''
    };

    const [formData, setFormData] = useState(initialFormState);
    const [uploading, setUploading] = useState(false);
    const [uploadError, setUploadError] = useState('');

    useEffect(() => {
        if (invitationData) {
            // Logika untuk mengisi form dari data edit (akan dikembangkan)
            // setFormData(invitationData);
        } else {
            setFormData(initialFormState);
        }
    }, [invitationData, show]);

    const handleChange = (e, section, index, field) => {
        const { name, value } = e.target;
        if (section) { // Untuk array dinamis seperti acara/amplop
            const newSectionData = [...formData[section]];
            newSectionData[index][field] = value;
            setFormData({ ...formData, [section]: newSectionData });
        } else if (name.includes('.')) { // Untuk objek nested seperti mempelai
            const [parent, child] = name.split('.');
            setFormData({ ...formData, [parent]: { ...formData[parent], [child]: value } });
        } else { // Untuk field biasa
            setFormData({ ...formData, [name]: value });
        }
    };
    
    const addItem = (section, item) => {
        setFormData({ ...formData, [section]: [...formData[section], item] });
    };

    const removeItem = (section, index) => {
        const newItems = formData[section].filter((_, i) => i !== index);
        setFormData({ ...formData, [section]: newItems });
    };

    const handleFileChange = async (e, type, field = null) => {
        const files = e.target.files;
        if (!files.length) return;

        setUploading(true);
        setUploadError('');

        for (const file of files) {
            const uploadData = new FormData();
            uploadData.append('file', file);
            uploadData.append('type', type);

            try {
                const response = await axios.post(`${API_URL}invitations/upload.php`, uploadData, {
                    headers: { 'Content-Type': 'multipart/form-data' },
                    withCredentials: true
                });
                const filePath = response.data.filePath;

                if (type === 'gallery') {
                    setFormData(prev => ({ ...prev, media: [...prev.media, filePath] }));
                } else if (type === 'cover') {
                    setFormData(prev => ({ ...prev, cover_slideshow: [...prev.cover_slideshow, filePath] }));
                } else if (type === 'couple' && field) {
                    const [parent, child] = field.split('.');
                    setFormData(prev => ({ ...prev, [parent]: { ...prev[parent], [child]: filePath } }));
                } else if (type === 'music' && field) {
                    setFormData(prev => ({ ...prev, [field]: filePath }));
                }
            } catch (error) {
                console.error('Gagal mengunggah file:', error);
                setUploadError(`Gagal mengunggah ${file.name}.`);
                break; // Hentikan jika ada error
            }
        }
        setUploading(false);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(formData);
    };

    return (
        <Modal show={show} onHide={onHide} size="lg" backdrop="static">
            <Modal.Header closeButton>
                <Modal.Title>{invitationData ? 'Edit Undangan' : 'Buat Undangan Baru'}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={handleSubmit}>
                    {/* ... (Fieldset Pengaturan Utama & Mempelai sama seperti sebelumnya) ... */}
                    
                    {/* Foto Sampul (Cover Slideshow) */}
                    <fieldset className="border p-3 mb-3">
                        <legend className="w-auto px-2">Foto Sampul (Cover)</legend>
                        <Form.Group>
                            <Form.Label>Upload Foto untuk Cover (bisa lebih dari satu)</Form.Label>
                            <Form.Control type="file" multiple onChange={(e) => handleFileChange(e, 'cover')} />
                        </Form.Group>
                        <div className="mt-2 d-flex flex-wrap gap-2">
                            {formData.cover_slideshow.map((img, i) => <Image key={i} src={`http://localhost/undangan_digital_platform/backend/uploads/cover/${img}`} thumbnail width="100" />)}
                        </div>
                    </fieldset>

                    {/* ... (Fieldset Detail Acara sama seperti sebelumnya) ... */}

                    {/* Musik Latar */}
                    <fieldset className="border p-3 mb-3">
                        <legend className="w-auto px-2">Musik Latar</legend>
                        <Form.Group>
                            <Form.Label>Upload Musik (MP3, WAV)</Form.Label>
                            <Form.Control type="file" accept=".mp3,.wav" onChange={(e) => handleFileChange(e, 'music', 'musik_latar')} />
                            {formData.musik_latar && <p className="mt-2">File terpilih: {formData.musik_latar}</p>}
                        </Form.Group>
                    </fieldset>

                    {/* Amplop Digital */}
                    <fieldset className="border p-3 mb-3">
                        <legend className="w-auto px-2">Amplop Digital</legend>
                        {formData.amplop.map((item, index) => (
                            <div key={index} className="dynamic-field-group">
                                <Form.Control className="mb-2" placeholder="Tipe Hadiah (cth: BCA, GoPay)" value={item.tipe_hadiah} onChange={(e) => handleChange(e, 'amplop', index, 'tipe_hadiah')} />
                                <Form.Control className="mb-2" placeholder="Nomor Rekening / No. HP" value={item.nomor_rekening} onChange={(e) => handleChange(e, 'amplop', index, 'nomor_rekening')} />
                                <Form.Control className="mb-2" placeholder="Atas Nama" value={item.atas_nama} onChange={(e) => handleChange(e, 'amplop', index, 'atas_nama')} />
                                {formData.amplop.length > 1 && <Button variant="danger" size="sm" className="btn-remove-item" onClick={() => removeItem('amplop', index)}>X</Button>}
                            </div>
                        ))}
                        <Button variant="outline-primary" onClick={() => addItem('amplop', { tipe_hadiah: '', nomor_rekening: '', atas_nama: '' })}>+ Tambah Hadiah</Button>
                    </fieldset>

                    {/* Galeri Foto & Video */}
                    <fieldset className="border p-3 mb-3">
                        <legend className="w-auto px-2">Galeri Foto</legend>
                        <Form.Group>
                            <Form.Label>Upload Media untuk Galeri (bisa lebih dari satu)</Form.Label>
                            <Form.Control type="file" multiple onChange={(e) => handleFileChange(e, 'gallery')} />
                        </Form.Group>
                         <div className="mt-2 d-flex flex-wrap gap-2">
                            {formData.media.map((img, i) => <Image key={i} src={`http://localhost/undangan_digital_platform/backend/uploads/gallery/${img}`} thumbnail width="100" />)}
                        </div>
                    </fieldset>

                    {/* Konten Tambahan */}
                    <fieldset className="border p-3 mb-3">
                        <legend className="w-auto px-2">Konten Tambahan</legend>
                        <Form.Group>
                            <Form.Label>Cerita Cinta</Form.Label>
                            <Form.Control as="textarea" rows={5} name="cerita" value={formData.cerita} onChange={handleChange} />
                        </Form.Group>
                    </fieldset>

                    {uploading && <div className="text-center my-3"><Spinner animation="border" /> <p>Mengunggah file...</p></div>}
                    {uploadError && <Alert variant="danger">{uploadError}</Alert>}

                    <Button variant="primary" type="submit" className="w-100" disabled={uploading}>
                        {uploading ? 'Menunggu file diunggah...' : 'Simpan Undangan'}
                    </Button>
                </Form>
            </Modal.Body>
        </Modal>
    );
};

export default InvitationForm;
