import React, { useState, useEffect } from 'react';
import { Container, Button, Table, Spinner, Alert, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

// Impor komponen form
import InvitationForm from '../components/InvitationForm';

// URL base untuk API, pastikan ini benar
const API_URL = 'http://localhost/undangan_digital_platform/backend/api/invitations/';

const DashboardPage = () => {
    const [invitations, setInvitations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    
    // State untuk mengontrol modal form
    const [showForm, setShowForm] = useState(false);
    const [editingInvitation, setEditingInvitation] = useState(null);

    const navigate = useNavigate();

    // Fungsi untuk mengambil semua undangan dari server
    const fetchInvitations = async () => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.get(`${API_URL}read_all.php`, { withCredentials: true });
            setInvitations(response.data.data || []);
        } catch (err) {
            setError('Gagal memuat data undangan. Pastikan backend berjalan dan Anda sudah login.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };
    
    // Ambil data saat komponen pertama kali dimuat
    useEffect(() => {
        fetchInvitations();
    }, []);

    // Fungsi untuk logout
    const handleLogout = () => {
        // Di aplikasi nyata, Anda akan memanggil API logout di sini
        navigate('/');
    };

    // Fungsi untuk menghapus undangan
    const handleDelete = async (id) => {
        if (window.confirm('Apakah Anda yakin ingin menghapus undangan ini? Semua data terkait akan hilang.')) {
            try {
                await axios.delete(`${API_URL}delete.php?id=${id}`, { withCredentials: true });
                alert('Undangan berhasil dihapus.');
                fetchInvitations(); // Muat ulang daftar undangan
            } catch (err) {
                alert('Gagal menghapus undangan.');
            }
        }
    };

    // Fungsi untuk menampilkan modal form
    const handleShowForm = (invitation = null) => {
        setEditingInvitation(invitation);
        setShowForm(true);
    };

    // Fungsi untuk menutup modal form
    const handleCloseForm = () => {
        setShowForm(false);
        setEditingInvitation(null);
    };

    // ====================================================================
    // INI ADALAH FUNGSI YANG ANDA MINTA (VERSI FINAL)
    // ====================================================================
    /**
     * Menangani penyimpanan data dari form.
     * Mengirim data ke endpoint 'create.php' jika membuat baru,
     * atau 'update.php' jika sedang mengedit.
     * @param {object} formData - Data state dari komponen InvitationForm.
     */
    const handleSaveInvitation = async (formData) => {
        try {
            let response;
            if (editingInvitation) {
                // Logika untuk UPDATE (akan dikembangkan nanti)
                // response = await axios.post(`${API_URL}update.php`, { ...formData, id: editingInvitation.id }, { withCredentials: true });
                alert('Fitur update belum diimplementasikan.');
                return; // Hentikan sementara
            } else {
                // Logika untuk CREATE, memanggil API yang sudah kita buat
                response = await axios.post(`${API_URL}create.php`, formData, { withCredentials: true });
            }

            // Jika berhasil
            alert(response.data.message);
            handleCloseForm();      // Tutup modal form
            fetchInvitations();     // Muat ulang daftar undangan agar data baru muncul
        } catch (err) {
            // Jika terjadi error
            console.error("Gagal menyimpan undangan:", err);
            alert(err.response?.data?.message || 'Terjadi kesalahan saat menyimpan data.');
        }
    };
    // ====================================================================

    return (
        <>
            <header className="dashboard-header">
                <h3>Dashboard Admin</h3>
                <Button variant="outline-danger" onClick={handleLogout}>Logout</Button>
            </header>

            <Container className="py-4">
                <Card className="mb-4">
                    <Card.Body>
                        <Card.Title>Manajemen Undangan</Card.Title>
                        <Card.Text>
                            Buat undangan baru atau kelola undangan yang sudah ada.
                        </Card.Text>
                        <Button variant="primary" onClick={() => handleShowForm()}>+ Buat Undangan Baru</Button>
                    </Card.Body>
                </Card>

                <Card>
                    <Card.Header as="h5">Daftar Undangan Tersimpan</Card.Header>
                    <Card.Body>
                        {loading && <div className="text-center my-5"><Spinner animation="border" /></div>}
                        {error && <Alert variant="danger">{error}</Alert>}
                        {!loading && !error && (
                            <Table striped bordered hover responsive>
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Judul</th>
                                        <th>Mempelai</th>
                                        <th style={{width: '350px'}}>Aksi</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {invitations.length > 0 ? (
                                        invitations.map(inv => (
                                            <tr key={inv.id}>
                                                <td>{inv.id}</td>
                                                <td>{inv.judul}</td>
                                                <td>{inv.mempelai}</td>
                                                <td>
                                                    <Button variant="info" size="sm" className="me-2">Tamu</Button>
                                                    <Button variant="success" size="sm" className="me-2">RSVP</Button>
                                                    <Button variant="warning" size="sm" className="me-2" onClick={() => handleShowForm(inv)}>Edit</Button>
                                                    <Button onClick={() => handleDelete(inv.id)} variant="danger" size="sm">Hapus</Button>
                                                </td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr>
                                            <td colSpan="4" className="text-center">Belum ada undangan yang dibuat.</td>
                                        </tr>
                                    )}
                                </tbody>
                            </Table>
                        )}
                    </Card.Body>
                </Card>
            </Container>

            {/* Modal Form untuk Create/Edit. Hanya ditampilkan jika showForm bernilai true */}
            {showForm && (
                <InvitationForm 
                    show={showForm}
                    onHide={handleCloseForm}
                    onSave={handleSaveInvitation}
                    invitationData={editingInvitation}
                />
            )}
        </>
    );
};

export default DashboardPage;
