import React, { useState, useEffect } from 'react';
import { Container, Button, Table, Spinner, Alert, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import InvitationForm from '../components/InvitationForm';
import GuestListModal from '../components/GuestListModal'; // <-- Impor komponen baru

const API_URL = 'http://localhost/undangan_digital_platform/backend/api/invitations/';

const DashboardPage = () => {
    // ... (semua state yang sudah ada tetap sama) ...
    const [invitations, setInvitations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [showForm, setShowForm] = useState(false);
    const [editingInvitation, setEditingInvitation] = useState(null);
    const navigate = useNavigate();

    // --- State baru untuk modal tamu ---
    const [showGuestModal, setShowGuestModal] = useState(false);
    const [selectedInvitation, setSelectedInvitation] = useState(null);


    const fetchInvitations = async () => {
        setLoading(true);
        try {
            const response = await axios.get(`${API_URL}read_all.php`, { withCredentials: true });
            setInvitations(response.data.data || []);
        } catch (err) {
            setError('Gagal memuat data undangan.');
        } finally {
            setLoading(false);
        }
    };
    
    useEffect(() => {
        fetchInvitations();
    }, []);

    // ... (fungsi handleLogout, handleDelete, handleShowForm, handleCloseForm, handleSaveInvitation tetap sama) ...
    const handleLogout = () => navigate('/');
    const handleDelete = async (id) => { /* ... */ };
    const handleShowForm = async (invitation = null) => { /* ... */ };
    const handleCloseForm = () => { /* ... */ };
    const handleSaveInvitation = async (formData) => { /* ... */ };


    // --- Fungsi baru untuk membuka modal tamu ---
    const handleShowGuestModal = (invitation) => {
        setSelectedInvitation(invitation);
        setShowGuestModal(true);
    };

    const handleCloseGuestModal = () => {
        setShowGuestModal(false);
        setSelectedInvitation(null);
    };


    return (
        <>
            <header className="dashboard-header">
                <h3>Dashboard Admin</h3>
                <Button variant="outline-danger" onClick={handleLogout}>Logout</Button>
            </header>

            <Container className="py-4">
                {/* ... (Card untuk Buat Undangan Baru tetap sama) ... */}

                <Card>
                    <Card.Header as="h5">Daftar Undangan Tersimpan</Card.Header>
                    <Card.Body>
                        {/* ... (Tabel undangan tetap sama, hanya tombolnya yang diubah) ... */}
                        <Table striped bordered hover responsive>
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Judul</th>
                                    <th>Mempelai</th>
                                    <th style={{width: '350px'}}>Aksi</th>
                                </tr>
                            </thead>
                            <tbody>
                                {invitations.length > 0 ? (
                                    invitations.map(inv => (
                                        <tr key={inv.id}>
                                            <td>{inv.id}</td>
                                            <td>{inv.judul}</td>
                                            <td>{inv.mempelai}</td>
                                            <td>
                                                {/* --- Tombol "Tamu" sekarang berfungsi --- */}
                                                <Button variant="info" size="sm" className="me-2" onClick={() => handleShowGuestModal(inv)}>Tamu</Button>
                                                <Button variant="success" size="sm" className="me-2">RSVP</Button>
                                                <Button variant="warning" size="sm" className="me-2" onClick={() => handleShowForm(inv)}>Edit</Button>
                                                <Button onClick={() => handleDelete(inv.id)} variant="danger" size="sm">Hapus</Button>
                                            </td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="4" className="text-center">Belum ada undangan yang dibuat.</td>
                                    </tr>
                                )}
                            </tbody>
                        </Table>
                    </Card.Body>
                </Card>
            </Container>

            {/* ... (Modal InvitationForm tetap sama) ... */}
            {showForm && ( <InvitationForm /* ... */ /> )}

            {/* --- Render Modal Tamu --- */}
            {selectedInvitation && (
                <GuestListModal 
                    show={showGuestModal}
                    onHide={handleCloseGuestModal}
                    invitation={selectedInvitation}
                />
            )}
        </>
    );
};

export default DashboardPage;
