import React, { useState, useEffect } from 'react';
import { Container, Button, Table, Spinner, Alert, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

// Impor komponen-komponen modal
import InvitationForm from '../components/InvitationForm';
import GuestListModal from '../components/GuestListModal';
import RsvpModal from '../components/RsvpModal';

const API_URL = 'http://localhost/undangan_digital_platform/backend/api/invitations/';

const DashboardPage = () => {
    const [invitations, setInvitations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // State untuk mengontrol modal mana yang aktif
    const [showFormModal, setShowFormModal] = useState(false);
    const [showGuestModal, setShowGuestModal] = useState(false);
    const [showRsvpModal, setShowRsvpModal] = useState(false);
    
    // State untuk menyimpan data undangan yang sedang diolah
    const [selectedInvitation, setSelectedInvitation] = useState(null);

    // Fungsi untuk mengambil daftar undangan dari server
    const fetchInvitations = async () => {
        setLoading(true);
        try {
            const response = await axios.get(`${API_URL}read_all.php`, { withCredentials: true });
            setInvitations(response.data.data || []);
        } catch (err) {
            setError('Gagal memuat data undangan. Pastikan backend berjalan.');
        } finally {
            setLoading(false);
        }
    };
    
    useEffect(() => {
        fetchInvitations();
    }, []);

    // --- Kumpulan Fungsi untuk Mengontrol Modal ---

    // Fungsi untuk menampilkan Form (baik untuk Buat Baru atau Edit)
    const handleShowForm = async (invitation = null) => {
        if (invitation) { // Jika ini mode EDIT
            try {
                const response = await axios.get(`${API_URL}read_single.php?id=${invitation.id}`, { withCredentials: true });
                setSelectedInvitation(response.data); // Simpan data lengkap untuk dioper ke form
            } catch (err) {
                alert('Gagal mengambil detail undangan untuk diedit.');
                return;
            }
        } else { // Jika ini mode BUAT BARU
            setSelectedInvitation(null); // Kosongkan data
        }
        setShowFormModal(true); // Tampilkan modal form
    };

    const handleCloseModals = () => {
        setShowFormModal(false);
        setShowGuestModal(false);
        setShowRsvpModal(false);
        setSelectedInvitation(null);
    };

    const handleSaveInvitation = async (formData) => {
        try {
            const endpoint = formData.id ? `${API_URL}update.php` : `${API_URL}create.php`;
            const response = await axios.post(endpoint, formData, { withCredentials: true });
            alert(response.data.message);
            handleCloseModals();
            fetchInvitations();
        } catch (err) {
            alert(err.response?.data?.message || 'Terjadi kesalahan saat menyimpan.');
        }
    };

    const handleShowGuestModal = (invitation) => {
        setSelectedInvitation(invitation);
        setShowGuestModal(true);
    };

    const handleShowRsvpModal = (invitation) => {
        setSelectedInvitation(invitation);
        setShowRsvpModal(true);
    };

    return (
        <>
            <header className="dashboard-header">
                <h3>Dashboard Admin</h3>
                <Button variant="outline-danger" onClick={() => navigate('/')}>Logout</Button>
            </header>

            <Container className="py-4">
                <Card className="mb-4">
                    <Card.Body>
                        <Card.Title>Manajemen Undangan</Card.Title>
                        <Card.Text>
                            Buat undangan baru atau kelola undangan yang sudah ada.
                        </Card.Text>
                        {/* INI ADALAH TOMBOL YANG DIMAKSUD DI TES 1.2 */}
                        <Button variant="primary" onClick={() => handleShowForm()}>+ Buat Undangan Baru</Button>
                    </Card.Body>
                </Card>

                <Card>
                    <Card.Header as="h5">Daftar Undangan Tersimpan</Card.Header>
                    <Card.Body>
                        {loading && <div className="text-center my-5"><Spinner animation="border" /></div>}
                        {error && <Alert variant="danger">{error}</Alert>}
                        {!loading && !error && (
                            <Table striped bordered hover responsive>
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Judul</th>
                                        <th>Mempelai</th>
                                        <th style={{width: '350px'}}>Aksi</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {invitations.length > 0 ? (
                                        invitations.map(inv => (
                                            <tr key={inv.id}>
                                                <td>{inv.id}</td>
                                                <td>{inv.judul}</td>
                                                <td>{inv.mempelai}</td>
                                                <td>
                                                    <Button variant="info" size="sm" className="me-2" onClick={() => handleShowGuestModal(inv)}>Tamu</Button>
                                                    <Button variant="success" size="sm" className="me-2" onClick={() => handleShowRsvpModal(inv)}>RSVP</Button>
                                                    <Button variant="warning" size="sm" className="me-2" onClick={() => handleShowForm(inv)}>Edit</Button>
                                                    {/* ... fungsi delete ... */}
                                                </td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr><td colSpan="4" className="text-center">Belum ada undangan yang dibuat.</td></tr>
                                    )}
                                </tbody>
                            </Table>
                        )}
                    </Card.Body>
                </Card>
            </Container>

            {/* Render semua modal di sini, mereka akan tampil/sembunyi berdasarkan state */}
            {showFormModal && (
                <InvitationForm 
                    show={showFormModal}
                    onHide={handleCloseModals}
                    onSave={handleSaveInvitation}
                    invitationData={selectedInvitation}
                />
            )}
            {showGuestModal && (
                <GuestListModal 
                    show={showGuestModal}
                    onHide={handleCloseModals}
                    invitation={selectedInvitation}
                />
            )}
            {showRsvpModal && (
                <RsvpModal
                    show={showRsvpModal}
                    onHide={handleCloseModals}
                    invitation={selectedInvitation}
                />
            )}
        </>
    );
};

export default DashboardPage;
