import React, { useState, useEffect } from 'react';
import { Modal, Button, Table, Spinner, Alert, Card } from 'react-bootstrap';
import api from '../api'; // Gunakan api.js

const RsvpModal = ({ show, onHide, invitation }) => {
    const [rsvps, setRsvps] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const fetchRsvps = async () => {
        if (!invitation) return;
        setLoading(true);
        setError('');
        try {
            const response = await api.get(`/rsvp/read.php?invitation_id=${invitation.id}`);
            setRsvps(response.data || []);
        } catch (err) {
            setError('Gagal memuat data RSVP.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => { if (show) { fetchRsvps(); } }, [show, invitation]);

